(50, 1, 'Interface Formulário Inclusão', 'interface-formulario-inclusao', NULL, '<h1 class=\"ui header\">#titulo#</h1>\r\n<!-- botoes < --><div class=\"ui basic right aligned segment\">\r\n    #botoes#\r\n</div><!-- botoes > -->\r\n<form id=\"#form-id#\" action=\"#form-action#\" method=\"post\" name=\"#form-name#\" class=\"ui form interfaceFormPadrao\">\r\n    <div id=\"_gestor-interface-insert-dados\">#form-page#</div>\r\n    <input id=\"_gestor-adicionar\" name=\"_gestor-adicionar\" type=\"hidden\" value=\"1\">\r\n    <input id=\"opcao\" name=\"opcao\" type=\"hidden\" value=\"adicionar\">\r\n    <div class=\"ui error message\"></div>\r\n    <div class=\"ui center aligned basic segment\">\r\n        <button id=\"_gestor-interface-insert-button\" data-tooltip=\"#form-button-title#\" data-position=\"right center\" data-inverted=\"\" class=\"positive ui button\">\r\n            #form-button-value#\r\n        </button>\r\n    </div>\r\n</form>', '#_gestor-interface-insert-dados{\r\n    margin-bottom:15px;\r\n}', 'A', 7, '2021-02-04 09:18:21', '2021-04-23 11:25:15'),
(51, 1, 'Interface Listar Sem Registros', 'interface-listar-sem-registros', NULL, '<div class=\"ui icon positive message\">\r\n    <i class=\"inbox icon\"></i>\r\n    <div class=\"content\">\r\n        <div class=\"header\">\r\n            Não há registros cadastrados neste módulo.\r\n        </div>\r\n        <p>Se disponível, basta selecionar o botão adicionar acima para criar um registro.</p>\r\n    </div>\r\n</div>', NULL, 'A', 1, '2021-02-03 10:38:37', '2021-02-04 10:06:54'),
(52, 1, 'Interface Listar', 'interface-listar', NULL, '<h1 class=\"ui header\">#titulo#</h1>\r\n<!-- botoes < --><div class=\"ui basic right aligned segment\">\r\n    #botoes#\r\n</div><!-- botoes > -->\r\n<div id=\"_gestor-interface-listar\" class=\"ui segment\">\r\n    <div class=\"ui stackable one column grid\">\r\n        <div class=\"column\" id=\"_gestor-interface-listar-column\">\r\n            #lista#\r\n        </div>\r\n    </div>\r\n</div>', '.dt-head-center {\r\n    text-align: center !important;\r\n}\r\n.table-responsive-fix{\r\n    overflow:hidden;\r\n}\r\n.segment > .button {\r\n    margin-bottom: 0.75em;\r\n}\r\n#_gestor-interface-listar{\r\n	margin-bottom: 1em;\r\n}', 'A', 6, '2021-01-06 14:00:33', '2022-02-02 18:45:23'),
(53, 1, 'Interface Listar Arquivos Sem Registros', 'interface-listar-arquivos-sem-registros', NULL, '<div class=\"ui icon positive message\">\r\n    <i class=\"inbox icon\"></i>\r\n    <div class=\"content\">\r\n        <div class=\"header\">\r\n            Não há registros cadastrados para esta filtragem.\r\n        </div>\r\n        <p>Favor tentar outra(s) opção(ões).</p>\r\n    </div>\r\n</div>', NULL, 'A', 1, '2021-06-04 17:45:38', '2021-06-04 17:45:38'),
(54, 7, 'Layout menutop-teste', 'menutop-teste', NULL, '  <div class=\"colunatop\">\r\n        <div class=\"colunatop ui three column padded stackable grid\">\r\n            <div class=\"row\">\r\n                <div class=\"three wide right aligned column\">\r\n                    <div class=\"logo\">\r\n                        <a href=\"#\">\r\n                            <img src=\"https://platform.b2make.com/images/gestor/logo.png\">\r\n                        </a>\r\n                    </div>\r\n                </div>\r\n                <div class=\"ten wide column\">\r\n                    <div class=\"ui grid\">\r\n                        <div class=\"column row\">\r\n                            <div class=\"center aligned two column row\">\r\n                                <div class=\"ui ordered steps\">\r\n                                    <div class=\"disabled step\">\r\n                                        <div class=\"content\">\r\n                                            <div class=\"title\">Carrinho</div>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class=\"disabled step\">\r\n                                        <div class=\"content\">\r\n                                            <div class=\"title\">Identificação</div>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class=\"active step\">\r\n                                        <div class=\"content\">\r\n                                            <div class=\"title\">Emissão</div>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class=\"disabled step\">\r\n                                        <div class=\"content\">\r\n                                            <div class=\"title\">Pagamento</div>\r\n  