m class=\"ui form attached fluid segment confirmacaoForm\" method=\"post\" action=\"/agendamentos/\">\r\n            <div class=\"ui primary large label\">\r\n                <i class=\"calendar check icon\"></i>\r\n                Data do Agendamento: @[[confirmacao-data]]@\r\n            </div>\r\n            <br>\r\n            <br>\r\n            <div class=\"ui icon buttons\">\r\n                <div class=\"ui icon buttons\">\r\n                    <a class=\"ui button red cancelarAgendamentoBtn\" data-content=\"Clique para Cancelar o Agendamento\" data-position=\"top left\" data-variation=\"inverted\">\r\n                        <i class=\"calendar minus outline icon\"></i>\r\n                        Cancelar\r\n                    </a>\r\n                    <a class=\"ui button green confirmarAgendamentoBtn\" data-content=\"Clique para Confirmar o Agendamento\" data-position=\"top left\" data-variation=\"inverted\">\r\n                        <i class=\"calendar check outline icon\"></i>\r\n                        Confirmar\r\n                    </a>\r\n                </div>\r\n            </div>\r\n            <input type=\"hidden\" name=\"efetuar_confirmacao\" value=\"1\">\r\n            <input type=\"hidden\" name=\"escolha\" value=\"cancelar\">\r\n            <input type=\"hidden\" name=\"acao\" value=\"confirmar\">\r\n            <input type=\"hidden\" name=\"agendamento_id\" value=\"@[[confirmacao-agendamento-id]]@\">\r\n        </form>\r\n        <div class=\"ui bottom attached warning message\">\r\n            <i class=\"icon exclamation triangle\"></i>\r\n            Importante: não é possível reverter o cancelamento deste agendamento!\r\n        </div>\r\n    </div>\r\n    <div class=\"cancelar escondido agendamentosTela\">\r\n        <div class=\"ui header\">Cancelar Agendamento</div>\r\n        <div class=\"ui icon attached warning message\">\r\n            <i class=\"calendar minus icon\"></i>\r\n            <div class=\"content\">\r\n       