/* Dashboard Cards Component Styles */

.dashboard-cards-segment {
    margin: 0 !important;
    padding: 2em !important;
}

/* Update Notification Message */
.dashboard-update-message {
    margin-bottom: 1.5em !important;
    border-radius: 8px !important;
    animation: dashboard-update-pulse 2s ease-in-out infinite;
}

@keyframes dashboard-update-pulse {
    0%, 100% {
        box-shadow: 0 0 0 0 rgba(20, 163, 197, 0.4);
    }
    50% {
        box-shadow: 0 0 0 10px rgba(20, 163, 197, 0);
    }
}

.dashboard-update-message .header {
    font-size: 1.1em !important;
}

.dashboard-update-actions {
    margin-top: 1em;
    display: flex;
    gap: 0.5em;
    flex-wrap: wrap;
}

/* Header Row with Search */
.dashboard-header-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1em;
    margin-bottom: 1.5em;
}

.dashboard-header-row .dashboard-cards-title {
    margin-bottom: 0 !important;
}

.dashboard-search-container {
    min-width: 250px;
    max-width: 350px;
}

.dashboard-search-container input {
    border-radius: 4px 0 0 4px !important;
}

#dashboard-search-reset {
    background: rgb(17, 36, 64) !important;
    color: #fff !important;
}

#dashboard-search-reset:hover {
    background: rgb(20, 163, 197) !important;
}

/* Card hidden by search */
.dashboard-module-card.search-hidden {
    display: none !important;
}

/* No results message */
.dashboard-no-results {
    text-align: center;
    padding: 3em 1em;
    color: #7f8c8d;
    font-size: 1.1em;
    grid-column: 1 / -1;
    width: 100%;
}

.dashboard-no-results i {
    display: block;
    font-size: 3em;
    margin-bottom: 0.5em;
    color: #bdc3c7;
}

.dashboard-cards-title {
    margin-bottom: 1.5em !important;
    color: #2c3e50 !important;
}

/* Sortable Cards Container */
.dashboard-sortable-cards {
    min-height: 200px;
}

/* Module Card Styles */
.dashboard-module-card {
    transition: transform 0.2s ease, box-shadow 0.2s ease !important;
    cursor: default;
    position: relative;
}

.dashboard-module-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(21, 39, 64, 0.7) !important;
    filter: brightness(0.98);
}
.dashboard-module-card:hover .dashboard-card-header {
    background: rgba(20, 163, 197, 0.2) !important;
}

/* Drag Handle */
.dashboard-card-drag-handle {
    position: absolute;
    top: 8px;
    right: 8px;
    cursor: grab;
    color: #bdc3c7;
    opacity: 0;
    transition: opacity 0.2s ease;
    z-index: 10;
}

.dashboard-module-card:hover .dashboard-card-drag-handle {
    opacity: 1;
}

.dashboard-card-drag-handle:hover {
    color: #7f8c8d;
}

.dashboard-card-drag-handle:active {
    cursor: grabbing;
}

/* Card Header with SVG */
.dashboard-card-header {
    background: rgb(17, 36, 64);
    padding: 1.5em !important;
    text-align: center;
    position: relative;
}

.dashboard-card-svg {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 80px;
}

.dashboard-card-svg svg {
    width: 80px;
    height: 80px;
    fill: #fff;
    filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.2));
}

/* Card Title */
.dashboard-card-title {
    font-size: 1.5em !important;
    color: #2c3e50 !important;
    transition: color 0.2s ease;
}

.dashboard-card-title:hover {
    color: #667eea !important;
}

/* Card Meta */
.dashboard-card-meta {
    margin-top: 0.5em !important;
}

.dashboard-card-meta .category {
    font-size: 0.85em;
    color: #95a5a6;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Card Description */
.dashboard-card-description {
    margin-top: 1em !important;
    font-size: 1.2em;
    color: #7f8c8d;
    line-height: 1.5;
    min-height: 3em;
    max-height: 4.5em;
    overflow: hidden;
    text-overflow: ellipsis;
}

/* Card Actions */
.dashboard-card-actions {
    display: flex !important;
    justify-content: space-between;
    align-items: center;
    background: #f8f9fa !important;
}

/* Container para botões à direita (manual e docs) */
.dashboard-card-actions-right {
    display: flex;
    gap: 0.3em;
    margin-left: auto;
}

/* Botão de manual */
.dashboard-btn-manual {
    padding: 0.5em 0.7em !important;
}

/* Botão de documentação */
.dashboard-btn-docs {
    padding: 0.5em 0.7em !important;
}

/* SortableJS Classes */
.sortable-ghost {
    opacity: 0.7;
    background: #e8f4fd !important;
    border: 2px dashed #667eea !important;
}

.sortable-chosen {
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.25) !important;
    transform: rotate(2deg);
}

.sortable-drag {
    opacity: 1 !important;
    cursor: grabbing !important;
}

/* Drop Placeholder */
.dashboard-card-placeholder {
    background: linear-gradient(135deg, #e8f4fd 0%, #f3e8ff 100%) !important;
    border: 2px dashed #667eea !important;
    border-radius: 0.28571429rem;
    min-height: 200px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.dashboard-card-placeholder::after {
    content: '';
    width: 50px;
    height: 50px;
    border: 3px dashed #667eea;
    border-radius: 50%;
}

/* Logout Section */
.dashboard-cards-divider {
    margin: 2em 0 !important;
    color: #95a5a6 !important;
}

.dashboard-logout-segment {
    padding: 1em 0 !important;
}

/* Module Group Colors */
.dashboard-module-card[data-module-group="administrativo"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="usuarios"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="conteudo"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="configuracao"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="ecommerce"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

/* Responsive Adjustments */
@media only screen and (max-width: 767px) {
    .dashboard-card-drag-handle {
        opacity: 0.5;
    }
    
    .dashboard-card-svg svg {
        width: 60px;
        height: 60px;
    }
    
    .dashboard-card-description {
        display: none;
    }
}

/* Animation for card reordering */
@keyframes cardDrop {
    0% {
        transform: scale(1.05);
        opacity: 0.8;
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

.dashboard-module-card.just-dropped {
    animation: cardDrop 0.3s ease-out;
}
/* ========================================
   SVG Theme Colors
   ======================================== */

/* Tema Escuro (Padrão) */
.dashboard-card-svg svg,
.dashboard-card-svg.svg-theme-dark svg {
    fill: #2c3e50;
    stroke: #2c3e50;
}

.dashboard-card-svg.svg-theme-dark svg path[fill="currentColor"],
.dashboard-card-svg svg path[fill="currentColor"] {
    fill: #2c3e50;
}

/* Tema Claro */
.dashboard-card-svg.svg-theme-light svg {
    fill: #ffffff;
    stroke: #ffffff;
}

.dashboard-card-svg.svg-theme-light svg path[fill="currentColor"] {
    fill: #ffffff;
}

/* Tema Azul */
.dashboard-card-svg.svg-theme-blue svg {
    fill: #667eea;
    stroke: #667eea;
}

.dashboard-card-svg.svg-theme-blue svg path[fill="currentColor"] {
    fill: #667eea;
}

/* Override para SVGs dentro do header com fundo colorido - Feather Icons usa stroke */
.dashboard-card-header .dashboard-card-svg svg {
    stroke: rgb(20, 163, 197);
}

/* ========================================
   Drag-and-Drop Enhancements
   ======================================== */

/* Desabilitar seleção de texto durante arraste */
.is-dragging,
.is-dragging * {
    user-select: none !important;
    -webkit-user-select: none !important;
    -moz-user-select: none !important;
    -ms-user-select: none !important;
}

.dashboard-module-card.sortable-chosen,
.dashboard-module-card.sortable-drag {
    user-select: none !important;
    -webkit-user-select: none !important;
}

/* Impedir seleção no handle */
.dashboard-card-drag-handle {
    user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}

/* ========================================
   Responsividade Customizada para Tablets
   ======================================== */

/* Tablets (768px - 1024px): 2 colunas ao invés de 4 */
@media screen and (min-width: 768px) and (max-width: 1024px) {
    .dashboard-sortable-cards .ui.card {
        width: calc(50% - 1em) !important;
        margin-left: 0.5em !important;
        margin-right: 0.5em !important;
    }

    .dashboard-sortable-cards .ui.card:nth-child(odd) {
        margin-left: 0 !important;
    }

    .dashboard-sortable-cards .ui.card:nth-child(even) {
        margin-right: 0 !important;
    }
}

/* Tablets em orientação portrait (até 768px): 1 coluna */
@media screen and (max-width: 767px) {
    .dashboard-sortable-cards .ui.card {
        width: 100% !important;
        margin-left: 0 !important;
        margin-right: 0 !important;
    }
}

/* =========================================
   Dashboard 3D - A-Frame Styles
   ========================================= */

/* Container Principal */
#dashboard-3d-wrapper {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    overflow: hidden;
    font-family: 'Segoe UI', system-ui, -apple-system, sans-serif;
}

/* Cena A-Frame */
#dashboard-3d-scene {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

/* =========================================
   Controles UI
   ========================================= */

#dashboard-3d-controls {
    position: fixed;
    top: 20px;
    left: 20px;
    z-index: 1000;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.control-btn {
    width: 48px;
    height: 48px;
    border: none;
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    color: #fff;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
    text-decoration: none;
}

.control-btn:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: scale(1.1);
}

.control-btn:active {
    transform: scale(0.95);
}

.control-btn.active {
    background: rgba(74, 158, 255, 0.4);
    box-shadow: 0 0 20px rgba(74, 158, 255, 0.5);
}

.control-btn svg {
    width: 24px;
    height: 24px;
}

#nav-controls {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-top: 20px;
}

/* =========================================
   Tooltip
   ========================================= */

#card-tooltip {
    position: fixed;
    padding: 16px 20px;
    background: rgba(20, 20, 40, 0.95);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border-radius: 12px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.5);
    z-index: 1000;
    pointer-events: none;
    max-width: 280px;
    transition: opacity 0.2s ease;
}

.tooltip-header {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 8px;
}

.tooltip-icon {
    font-size: 24px;
}

.tooltip-title {
    font-size: 16px;
    font-weight: 600;
    color: #fff;
}

.tooltip-group {
    display: block;
    font-size: 12px;
    color: #4a9eff;
    margin-bottom: 8px;
}

.tooltip-description {
    font-size: 13px;
    color: #aaa;
    line-height: 1.4;
    margin: 0 0 10px 0;
}

.tooltip-actions {
    padding-top: 8px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.tooltip-hint {
    font-size: 11px;
    color: #666;
    display: flex;
    align-items: center;
    gap: 5px;
}

.tooltip-hint::before {
    content: '👆';
}

/* =========================================
   Modal de Ações
   ========================================= */

#module-action-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 2000;
    display: flex;
    align-items: center;
    justify-content: center;
}

.modal-backdrop {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.7);
    backdrop-filter: blur(5px);
    -webkit-backdrop-filter: blur(5px);
}

.modal-content {
    position: relative;
    background: linear-gradient(145deg, rgba(30, 30, 60, 0.98), rgba(20, 20, 40, 0.98));
    border-radius: 20px;
    padding: 30px;
    min-width: 350px;
    max-width: 450px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
    animation: modalSlideIn 0.3s ease;
}

@keyframes modalSlideIn {
    from {
        opacity: 0;
        transform: scale(0.9) translateY(20px);
    }
    to {
        opacity: 1;
        transform: scale(1) translateY(0);
    }
}

.modal-header {
    display: flex;
    align-items: center;
    gap: 15px;
    margin-bottom: 15px;
}

.modal-icon {
    width: 50px;
    height: 50px;
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
    background: #4a9eff;
}

.modal-header h3 {
    flex: 1;
    margin: 0;
    font-size: 20px;
    color: #fff;
}

.close-btn {
    width: 36px;
    height: 36px;
    border: none;
    border-radius: 8px;
    background: rgba(255, 255, 255, 0.1);
    color: #fff;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.close-btn:hover {
    background: rgba(255, 100, 100, 0.3);
}

.close-btn svg {
    width: 18px;
    height: 18px;
}

.modal-description {
    color: #aaa;
    font-size: 14px;
    line-height: 1.5;
    margin: 0 0 20px 0;
}

.modal-buttons {
    display: flex;
    gap: 12px;
    margin-bottom: 15px;
}

.modal-btn {
    flex: 1;
    padding: 14px 20px;
    border: none;
    border-radius: 12px;
    cursor: pointer;
    font-size: 14px;
    font-weight: 500;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    transition: all 0.2s ease;
}

.modal-btn svg {
    width: 18px;
    height: 18px;
}

.modal-btn-primary {
    background: linear-gradient(135deg, #4a9eff, #2d7dd2);
    color: #fff;
}

.modal-btn-primary:hover {
    background: linear-gradient(135deg, #5aaeFF, #3d8de2);
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(74, 158, 255, 0.4);
}

.modal-btn-secondary {
    background: rgba(109, 212, 0, 0.2);
    color: #6dd400;
    border: 1px solid rgba(109, 212, 0, 0.3);
}

.modal-btn-secondary:hover {
    background: rgba(109, 212, 0, 0.3);
    transform: translateY(-2px);
}

.modal-custom-actions {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.modal-custom-actions:empty {
    display: none;
}

.modal-btn-custom {
    width: 100%;
    padding: 12px 16px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 10px;
    background: rgba(255, 255, 255, 0.05);
    color: #fff;
    cursor: pointer;
    font-size: 14px;
    text-align: left;
    transition: all 0.2s ease;
}

.modal-btn-custom:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.3);
}

/* =========================================
   Menu Pizza
   ========================================= */

#pizza-menu {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 3000;
}

.pizza-backdrop {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.pizza-container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 350px;
    height: 350px;
}

.pizza-center {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 80px;
    height: 80px;
    background: linear-gradient(145deg, rgba(40, 40, 80, 0.9), rgba(20, 20, 50, 0.9));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 2px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.5);
    z-index: 10;
}

.pizza-close-btn {
    width: 50px;
    height: 50px;
    border: none;
    border-radius: 50%;
    background: rgba(255, 100, 100, 0.2);
    color: #ff6b6b;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.pizza-close-btn:hover {
    background: rgba(255, 100, 100, 0.4);
    transform: scale(1.1);
}

.pizza-close-btn svg {
    width: 24px;
    height: 24px;
}

.pizza-items {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 100%;
    height: 100%;
}

.pizza-item {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(calc(-50% + var(--x)), calc(-50% + var(--y)));
    width: 70px;
    height: 70px;
    border: none;
    border-radius: 50%;
    background: linear-gradient(145deg, rgba(60, 60, 100, 0.9), rgba(30, 30, 60, 0.9));
    color: #fff;
    cursor: pointer;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 4px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3);
    transition: all 0.3s ease;
    animation: pizzaItemIn 0.4s ease backwards;
}

.pizza-item:hover {
    background: linear-gradient(145deg, rgba(80, 80, 140, 0.9), rgba(40, 40, 80, 0.9));
    transform: translate(calc(-50% + var(--x)), calc(-50% + var(--y))) scale(1.15);
    box-shadow: 0 8px 30px rgba(74, 158, 255, 0.3);
}

@keyframes pizzaItemIn {
    from {
        opacity: 0;
        transform: translate(-50%, -50%) scale(0);
    }
    to {
        opacity: 1;
    }
}

.pizza-icon {
    font-size: 24px;
}

.pizza-label {
    position: absolute;
    top: -35px;
    white-space: nowrap;
    font-size: 12px;
    background: rgba(0, 0, 0, 0.8);
    padding: 4px 10px;
    border-radius: 6px;
    opacity: 0;
    transition: opacity 0.2s ease;
    pointer-events: none;
}

.pizza-item:hover .pizza-label {
    opacity: 1;
}

/* =========================================
   Container do Módulo (iframe)
   ========================================= */

#module-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #0a0a1a;
    z-index: 4000;
    display: flex;
    flex-direction: column;
}

.module-header {
    display: flex;
    align-items: center;
    gap: 15px;
    padding: 12px 20px;
    background: rgba(20, 20, 40, 0.95);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.module-back-btn {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 10px 16px;
    border: none;
    border-radius: 8px;
    background: rgba(255, 255, 255, 0.1);
    color: #fff;
    cursor: pointer;
    font-size: 14px;
    transition: all 0.2s ease;
}

.module-back-btn:hover {
    background: rgba(255, 255, 255, 0.2);
}

.module-back-btn svg {
    width: 18px;
    height: 18px;
}

.module-header h3 {
    flex: 1;
    margin: 0;
    font-size: 16px;
    color: #fff;
}

.module-expand-btn {
    width: 40px;
    height: 40px;
    border: none;
    border-radius: 8px;
    background: rgba(255, 255, 255, 0.1);
    color: #fff;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.module-expand-btn:hover {
    background: rgba(74, 158, 255, 0.3);
}

.module-expand-btn svg {
    width: 18px;
    height: 18px;
}

#module-iframe {
    flex: 1;
    width: 100%;
    border: none;
    background: #fff;
}

/* =========================================
   Loading Overlay
   ========================================= */

#loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(145deg, #0a0a1a, #1a1a3a);
    z-index: 5000;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: opacity 0.5s ease;
}

.loading-content {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 30px;
}

.loading-ring {
    position: relative;
    width: 120px;
    height: 120px;
    margin-bottom: 15px;
}

.loading-segment {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 50px;
    height: 10px;
    background: var(--color);
    border-radius: 5px;
    transform-origin: -10px center;
    animation: loadingRotate 2s ease-in-out infinite;
    animation-delay: calc(var(--i) * 0.15s);
}

.loading-segment:nth-child(1) { --color: #4a9eff; --i: 0; transform: rotate(0deg) translateX(30px); }
.loading-segment:nth-child(2) { --color: #6dd400; --i: 1; transform: rotate(60deg) translateX(30px); }
.loading-segment:nth-child(3) { --color: #a855f7; --i: 2; transform: rotate(120deg) translateX(30px); }
.loading-segment:nth-child(4) { --color: #f97316; --i: 3; transform: rotate(180deg) translateX(30px); }
.loading-segment:nth-child(5) { --color: #14b8a6; --i: 4; transform: rotate(240deg) translateX(30px); }
.loading-segment:nth-child(6) { --color: #ec4899; --i: 5; transform: rotate(300deg) translateX(30px); }

@keyframes loadingRotate {
    0%, 100% { opacity: 0.3; transform: rotate(calc(var(--i) * 60deg)) translateX(30px) scale(1); }
    50% { opacity: 1; transform: rotate(calc(var(--i) * 60deg)) translateX(30px) scale(1.2); }
}

.loading-logo {
    width: 350px;
    height: auto;
    margin-bottom: 50px;
}

.loading-text {
    font-size: 16px;
    color: #fff;
    letter-spacing: 1px;
}

.loading-progress {
    width: 200px;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    overflow: hidden;
}

.loading-progress-bar {
    height: 100%;
    background: linear-gradient(90deg, #4a9eff, #6dd400, #a855f7);
    background-size: 200% 100%;
    animation: loadingProgress 1.5s ease-in-out infinite;
}

@keyframes loadingProgress {
    0% { width: 0%; background-position: 0% 0%; }
    50% { width: 70%; }
    100% { width: 100%; background-position: 100% 0%; }
}

/* =========================================
   Legenda dos Grupos
   ========================================= */

#groups-legend {
    position: fixed;
    top: 280px;
    left: 80px;
    background: rgba(20, 20, 40, 0.9);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border-radius: 12px;
    padding: 15px;
    min-width: 180px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3);
    z-index: 100;
}

#groups-legend h4 {
    display: flex;
    align-items: center;
    gap: 8px;
    margin: 0 0 12px 0;
    font-size: 13px;
    color: #888;
    text-transform: uppercase;
    letter-spacing: 1px;
}

#legend-items {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.legend-item {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 8px 10px;
    border-radius: 8px;
    cursor: pointer;
    transition: all 0.2s ease;
}

.legend-item:hover {
    background: rgba(255, 255, 255, 0.1);
}

.legend-color {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    flex-shrink: 0;
}

.legend-name {
    flex: 1;
    font-size: 13px;
    color: #fff;
}

.legend-count {
    font-size: 11px;
    color: #666;
    background: rgba(255, 255, 255, 0.1);
    padding: 2px 8px;
    border-radius: 10px;
}

/* =========================================
   Instruções de Uso
   ========================================= */

#usage-hints {
    position: fixed;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 20px;
    z-index: 100;
}

.hint {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 12px;
    color: #666;
}

.hint kbd {
    background: rgba(255, 255, 255, 0.1);
    padding: 4px 8px;
    border-radius: 4px;
    font-family: inherit;
    color: #aaa;
}

/* =========================================
   Atalhos de Teclado
   ========================================= */

#keyboard-shortcuts {
    position: fixed;
    top: 336px;
    left: 80px;
    background: rgba(20, 20, 40, 0.9);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border-radius: 12px;
    padding: 15px;
    min-width: 180px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3);
    z-index: 100;
}

#keyboard-shortcuts h4 {
    display: flex;
    align-items: center;
    gap: 8px;
    margin: 0 0 12px 0;
    font-size: 13px;
    color: #888;
    text-transform: uppercase;
    letter-spacing: 1px;
}

#shortcuts-list {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.shortcut {
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: 13px;
    color: #aaa;
}

.shortcut kbd {
    min-width: 28px;
    padding: 4px 8px;
    background: rgba(74, 158, 255, 0.2);
    border-radius: 4px;
    font-family: inherit;
    color: #4a9eff;
    text-align: center;
    font-weight: 600;
}

/* =========================================
   Botão 2D (voltar)
   ========================================= */

.control-btn-text {
    font-weight: bold;
    font-size: 14px;
}

.btn-text-2d {
    font-size: 16px;
    font-weight: 700;
    color: #4a9eff;
    text-shadow: 0 0 10px rgba(74, 158, 255, 0.5);
}

.control-btn-text:hover .btn-text-2d {
    color: #fff;
    text-shadow: 0 0 15px rgba(255, 255, 255, 0.8);
}

/* =========================================
   Indicador de Zoom
   ========================================= */

#zoom-indicator {
    position: fixed;
    bottom: 20px;
    left: 20px;
    z-index: 100;
    background: rgba(20, 20, 40, 0.8);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    padding: 8px 16px;
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

#zoom-level {
    font-size: 13px;
    font-weight: 600;
    color: #4a9eff;
}

/* =========================================
   Utilidades
   ========================================= */

.hidden {
    display: none !important;
}

.a-enter-vr-button {
    right: 15px !important;
    bottom: 60px !important;
}

/* =========================================
   Responsivo
   ========================================= */

@media (max-width: 768px) {
    #dashboard-3d-controls {
        top: 10px;
        left: 10px;
    }
    
    .control-btn {
        width: 40px;
        height: 40px;
    }
    
    #groups-legend {
        bottom: 10px;
        right: 10px;
        left: 10px;
        min-width: auto;
    }
    
    #usage-hints {
        display: none;
    }
    
    .modal-content {
        margin: 20px;
        min-width: auto;
    }
    
    .pizza-container {
        width: 300px;
        height: 300px;
    }
    
    .pizza-item {
        width: 55px;
        height: 55px;
    }
}

/* Dashboard Cards Component Styles */

.dashboard-cards-segment {
    margin: 0 !important;
    padding: 2em !important;
}

/* Update Notification Message */
.dashboard-update-message {
    margin-bottom: 1.5em !important;
    border-radius: 8px !important;
    animation: dashboard-update-pulse 2s ease-in-out infinite;
}

@keyframes dashboard-update-pulse {
    0%, 100% {
        box-shadow: 0 0 0 0 rgba(20, 163, 197, 0.4);
    }
    50% {
        box-shadow: 0 0 0 10px rgba(20, 163, 197, 0);
    }
}

.dashboard-update-message .header {
    font-size: 1.1em !important;
}

.dashboard-update-actions {
    margin-top: 1em;
    display: flex;
    gap: 0.5em;
    flex-wrap: wrap;
}

/* Header Row with Search */
.dashboard-header-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1em;
    margin-bottom: 1.5em;
}

.dashboard-header-row .dashboard-cards-title {
    margin-bottom: 0 !important;
}

.dashboard-search-container {
    min-width: 250px;
    max-width: 350px;
}

.dashboard-search-container input {
    border-radius: 4px 0 0 4px !important;
}

#dashboard-search-reset {
    background: rgb(17, 36, 64) !important;
    color: #fff !important;
}

#dashboard-search-reset:hover {
    background: rgb(20, 163, 197) !important;
}

/* Card hidden by search */
.dashboard-module-card.search-hidden {
    display: none !important;
}

/* No results message */
.dashboard-no-results {
    text-align: center;
    padding: 3em 1em;
    color: #7f8c8d;
    font-size: 1.1em;
    grid-column: 1 / -1;
    width: 100%;
}

.dashboard-no-results i {
    display: block;
    font-size: 3em;
    margin-bottom: 0.5em;
    color: #bdc3c7;
}

.dashboard-cards-title {
    margin-bottom: 1.5em !important;
    color: #2c3e50 !important;
}

/* Sortable Cards Container */
.dashboard-sortable-cards {
    min-height: 200px;
}

/* Module Card Styles */
.dashboard-module-card {
    transition: transform 0.2s ease, box-shadow 0.2s ease !important;
    cursor: default;
    position: relative;
}

.dashboard-module-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(21, 39, 64, 0.7) !important;
    filter: brightness(0.98);
}
.dashboard-module-card:hover .dashboard-card-header {
    background: rgba(20, 163, 197, 0.2) !important;
}

/* Drag Handle */
.dashboard-card-drag-handle {
    position: absolute;
    top: 8px;
    right: 8px;
    cursor: grab;
    color: #bdc3c7;
    opacity: 0;
    transition: opacity 0.2s ease;
    z-index: 10;
}

.dashboard-module-card:hover .dashboard-card-drag-handle {
    opacity: 1;
}

.dashboard-card-drag-handle:hover {
    color: #7f8c8d;
}

.dashboard-card-drag-handle:active {
    cursor: grabbing;
}

/* Card Header with SVG */
.dashboard-card-header {
    background: rgb(17, 36, 64);
    padding: 1.5em !important;
    text-align: center;
    position: relative;
}

.dashboard-card-svg {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 80px;
}

.dashboard-card-svg svg {
    width: 80px;
    height: 80px;
    fill: #fff;
    filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.2));
}

/* Card Title */
.dashboard-card-title {
    font-size: 1.5em !important;
    color: #2c3e50 !important;
    transition: color 0.2s ease;
}

.dashboard-card-title:hover {
    color: #667eea !important;
}

/* Card Meta */
.dashboard-card-meta {
    margin-top: 0.5em !important;
}

.dashboard-card-meta .category {
    font-size: 0.85em;
    color: #95a5a6;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Card Description */
.dashboard-card-description {
    margin-top: 1em !important;
    font-size: 1.2em;
    color: #7f8c8d;
    line-height: 1.5;
    min-height: 3em;
    max-height: 4.5em;
    overflow: hidden;
    text-overflow: ellipsis;
}

/* Card Actions */
.dashboard-card-actions {
    display: flex !important;
    justify-content: space-between;
    align-items: center;
    background: #f8f9fa !important;
}

/* Container para botões à direita (manual e docs) */
.dashboard-card-actions-right {
    display: flex;
    gap: 0.3em;
    margin-left: auto;
}

/* Botão de manual */
.dashboard-btn-manual {
    padding: 0.5em 0.7em !important;
}

/* Botão de documentação */
.dashboard-btn-docs {
    padding: 0.5em 0.7em !important;
}

/* SortableJS Classes */
.sortable-ghost {
    opacity: 0.7;
    background: #e8f4fd !important;
    border: 2px dashed #667eea !important;
}

.sortable-chosen {
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.25) !important;
    transform: rotate(2deg);
}

.sortable-drag {
    opacity: 1 !important;
    cursor: grabbing !important;
}

/* Drop Placeholder */
.dashboard-card-placeholder {
    background: linear-gradient(135deg, #e8f4fd 0%, #f3e8ff 100%) !important;
    border: 2px dashed #667eea !important;
    border-radius: 0.28571429rem;
    min-height: 200px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.dashboard-card-placeholder::after {
    content: '';
    width: 50px;
    height: 50px;
    border: 3px dashed #667eea;
    border-radius: 50%;
}

/* Logout Section */
.dashboard-cards-divider {
    margin: 2em 0 !important;
    color: #95a5a6 !important;
}

.dashboard-logout-segment {
    padding: 1em 0 !important;
}

/* Module Group Colors */
.dashboard-module-card[data-module-group="administrativo"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="usuarios"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="conteudo"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="configuracao"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

.dashboard-module-card[data-module-group="ecommerce"] .dashboard-card-header {
    background: rgb(20, 163, 197);
}

/* Responsive Adjustments */
@media only screen and (max-width: 767px) {
    .dashboard-card-drag-handle {
        opacity: 0.5;
    }
    
    .dashboard-card-svg svg {
        width: 60px;
        height: 60px;
    }
    
    .dashboard-card-description {
        display: none;
    }
}

/* Animation for card reordering */
@keyframes cardDrop {
    0% {
        transform: scale(1.05);
        opacity: 0.8;
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

.dashboard-module-card.just-dropped {
    animation: cardDrop 0.3s ease-out;
}
/* ========================================
   SVG Theme Colors
   ======================================== */

/* Tema Escuro (Padrão) */
.dashboard-card-svg svg,
.dashboard-card-svg.svg-theme-dark svg {
    fill: #2c3e50;
    stroke: #2c3e50;
}

.dashboard-card-svg.svg-theme-dark svg path[fill="currentColor"],
.dashboard-card-svg svg path[fill="currentColor"] {
    fill: #2c3e50;
}

/* Tema Claro */
.dashboard-card-svg.svg-theme-light svg {
    fill: #ffffff;
    stroke: #ffffff;
}

.dashboard-card-svg.svg-theme-light svg path[fill="currentColor"] {
    fill: #ffffff;
}

/* Tema Azul */
.dashboard-card-svg.svg-theme-blue svg {
    fill: #667eea;
    stroke: #667eea;
}

.dashboard-card-svg.svg-theme-blue svg path[fill="currentColor"] {
    fill: #667eea;
}

/* Override para SVGs dentro do header com fundo colorido - Feather Icons usa stroke */
.dashboard-card-header .dashboard-card-svg svg {
    stroke: rgb(20, 163, 197);
}

/* ========================================
   Drag-and-Drop Enhancements
   ======================================== */

/* Desabilitar seleção de texto durante arraste */
.is-dragging,
.is-dragging * {
    user-select: none !important;
    -webkit-user-select: none !important;
    -moz-user-select: none !important;
    -ms-user-select: none !important;
}

.dashboard-module-card.sortable-chosen,
.dashboard-module-card.sortable-drag {
    user-select: none !important;
    -webkit-user-select: none !important;
}

/* Impedir seleção no handle */
.dashboard-card-drag-handle {
    user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}

/* ========================================
   Responsividade Customizada para Tablets
   ======================================== */

/* Tablets (768px - 1024px): 2 colunas ao invés de 4 */
@media screen and (min-width: 768px) and (max-width: 1024px) {
    .dashboard-sortable-cards .ui.card {
        width: calc(50% - 1em) !important;
        margin-left: 0.5em !important;
        margin-right: 0.5em !important;
    }

    .dashboard-sortable-cards .ui.card:nth-child(odd) {
        margin-left: 0 !important;
    }

    .dashboard-sortable-cards .ui.card:nth-child(even) {
        margin-right: 0 !important;
    }
}

/* Tablets em orientação portrait (até 768px): 1 coluna */
@media screen and (max-width: 767px) {
    .dashboard-sortable-cards .ui.card {
        width: 100% !important;
        margin-left: 0 !important;
        margin-right: 0 !important;
    }
}

/* =========================================
   Dashboard 3D - A-Frame Styles
   ========================================= */

/* Container Principal */
#dashboard-3d-wrapper {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    overflow: hidden;
    font-family: 'Segoe UI', system-ui, -apple-system, sans-serif;
}

/* Cena A-Frame */
#dashboard-3d-scene {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

/* =========================================
   Controles UI
   ========================================= */

#dashboard-3d-controls {
    position: fixed;
    top: 20px;
    left: 20px;
    z-index: 1000;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.control-btn {
    width: 48px;
    height: 48px;
    border: none;
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    color: #fff;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
    text-decoration: none;
}

.control-btn:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: scale(1.1);
}

.control-btn:active {
    transform: scale(0.95);
}

.control-btn.active {
    background: rgba(74, 158, 255, 0.4);
    box-shadow: 0 0 20px rgba(74, 158, 255, 0.5);
}

.control-btn svg {
    width: 24px;
    height: 24px;
}

#nav-controls {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-top: 20px;
}

/* =========================================
   Tooltip
   ========================================= */

#card-tooltip {
    position: fixed;
    padding: 16px 20px;
    background: rgba(20, 20, 40, 0.95);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border-radius: 12px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.5);
    z-index: 1000;
    pointer-events: none;
    max-width: 280px;
    transition: opacity 0.2s ease;
}

.tooltip-header {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 8px;
}

.tooltip-icon {
    font-size: 24px;
}

.tooltip-title {
    font-size: 16px;
    font-weight: 600;
    color: #fff;
}

.tooltip-group {
    display: block;
    font-size: 12px;
    color: #4a9eff;
    margin-bottom: 8px;
}

.tooltip-description {
    font-size: 13px;
    color: #aaa;
    line-height: 1.4;
    margin: 0 0 10px 0;
}

.tooltip-actions {
    padding-top: 8px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.tooltip-hint {
    font-size: 11px;
    color: #666;
    display: flex;
    align-items: center;
    gap: 5px;
}

.tooltip-hint::before {
    content: '👆';
}

/* =========================================
   Modal de Ações
   ========================================= */

#module-action-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 2000;
    display: flex;
    align-items: center;
    justify-content: center;
}

.modal-backdrop {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.7);
    backdrop-filter: blur(5px);
    -webkit-backdrop-filter: blur(5px);
}

.modal-content {
    position: relative;
    background: linear-gradient(145deg, rgba(30, 30, 60, 0.98), rgba(20, 20, 40, 0.98));
    border-radius: 20px;
    padding: 30px;
    min-width: 350px;
    max-width: 450px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
    animation: modalSlideIn 0.3s ease;
}

@keyframes modalSlideIn {
    from {
        opacity: 0;
        transform: scale(0.9) translateY(20px);
    }
    to {
        opacity: 1;
        transform: scale(1) translateY(0);
    }
}

.modal-header {
    display: flex;
    align-items: center;
    gap: 15px;
    margin-bottom: 15px;
}

.modal-icon {
    width: 50px;
    height: 50px;
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
    background: #4a9eff;
}

.modal-header h3 {
    flex: 1;
    margin: 0;
    font-size: 20px;
    color: #fff;
}

.close-btn {
    width: 36px;
    height: 36px;
    border: none;
    border-radius: 8px;
    background: rgba(255, 255, 255, 0.1);
    color: #fff;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.close-btn:hover {
    background: rgba(255, 100, 100, 0.3);
}

.close-btn svg {
    width: 18px;
    height: 18px;
}

.modal-description {
    color: #aaa;
    font-size: 14px;
    line-height: 1.5;
    margin: 0 0 20px 0;
}

.modal-buttons {
    display: flex;
    gap: 12px;
    margin-bottom: 15px;
}

.modal-btn {
    flex: 1;
    padding: 14px 20px;
    border: none;
    border-radius: 12px;
    cursor: pointer;
    font-size: 14px;
    font-weight: 500;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    transition: all 0.2s ease;
}

.modal-btn svg {
    width: 18px;
    height: 18px;
}

.modal-btn-primary {
    background: linear-gradient(135deg, #4a9eff, #2d7dd2);
    color: #fff;
}

.modal-btn-primary:hover {
    background: linear-gradient(135deg, #5aaeFF, #3d8de2);
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(74, 158, 255, 0.4);
}

.modal-btn-secondary {
    background: rgba(109, 212, 0, 0.2);
    color: #6dd400;
    border: 1px solid rgba(109, 212, 0, 0.3);
}

.modal-btn-secondary:hover {
    background: rgba(109, 212, 0, 0.3);
    transform: translateY(-2px);
}

.modal-custom-actions {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.modal-custom-actions:empty {
    display: none;
}

.modal-btn-custom {
    width: 100%;
    padding: 12px 16px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 10px;
    background: rgba(255, 255, 255, 0.05);
    color: #fff;
    cursor: pointer;
    font-size: 14px;
    text-align: left;
    transition: all 0.2s ease;
}

.modal-btn-custom:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.3);
}

/* =========================================
   Menu Pizza
   ========================================= */

#pizza-menu {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 3000;
}

.pizza-backdrop {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.pizza-container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 350px;
    height: 350px;
}

.pizza-center {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 80px;
    height: 80px;
    background: linear-gradient(145deg, rgba(40, 40, 80, 0.9), rgba(20, 20, 50, 0.9));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 2px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.5);
    z-index: 10;
}

.pizza-close-btn {
    width: 50px;
    height: 50px;
    border: none;
    border-radius: 50%;
    background: rgba(255, 100, 100, 0.2);
    color: #ff6b6b;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.pizza-close-btn:hover {
    background: rgba(255, 100, 100, 0.4);
    transform: scale(1.1);
}

.pizza-close-btn svg {
    width: 24px;
    height: 24px;
}

.pizza-items {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 100%;
    height: 100%;
}

.pizza-item {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(calc(-50% + var(--x)), calc(-50% + var(--y)));
    width: 70px;
    height: 70px;
    border: none;
    border-radius: 50%;
    background: linear-gradient(145deg, rgba(60, 60, 100, 0.9), rgba(30, 30, 60, 0.9));
    color: #fff;
    cursor: pointer;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 4px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3);
    transition: all 0.3s ease;
    animation: pizzaItemIn 0.4s ease backwards;
}

.pizza-item:hover {
    background: linear-gradient(145deg, rgba(80, 80, 140, 0.9), rgba(40, 40, 80, 0.9));
    transform: translate(calc(-50% + var(--x)), calc(-50% + var(--y))) scale(1.15);
    box-shadow: 0 8px 30px rgba(74, 158, 255, 0.3);
}

@keyframes pizzaItemIn {
    from {
        opacity: 0;
        transform: translate(-50%, -50%) scale(0);
    }
    to {
        opacity: 1;
    }
}

.pizza-icon {
    font-size: 24px;
}

.pizza-label {
    position: absolute;
    top: -35px;
    white-space: nowrap;
    font-size: 12px;
    background: rgba(0, 0, 0, 0.8);
    padding: 4px 10px;
    border-radius: 6px;
    opacity: 0;
    transition: opacity 0.2s ease;
    pointer-events: none;
}

.pizza-item:hover .pizza-label {
    opacity: 1;
}

