/* =========================================
   Loading Overlay
   ========================================= */

#loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(145deg, #0a0a1a, #1a1a3a);
    z-index: 5000;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: opacity 0.5s ease;
}

.loading-content {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 30px;
}

.loading-ring {
    position: relative;
    width: 120px;
    height: 120px;
    margin-bottom: 15px;
}

.loading-segment {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 50px;
    height: 10px;
    background: var(--color);
    border-radius: 5px;
    transform-origin: -10px center;
    animation: loadingRotate 2s ease-in-out infinite;
    animation-delay: calc(var(--i) * 0.15s);
}

.loading-segment:nth-child(1) { --color: #4a9eff; --i: 0; transform: rotate(0deg) translateX(30px); }
.loading-segment:nth-child(2) { --color: #6dd400; --i: 1; transform: rotate(60deg) translateX(30px); }
.loading-segment:nth-child(3) { --color: #a855f7; --i: 2; transform: rotate(120deg) translateX(30px); }
.loading-segment:nth-child(4) { --color: #f97316; --i: 3; transform: rotate(180deg) translateX(30px); }
.loading-segment:nth-child(5) { --color: #14b8a6; --i: 4; transform: rotate(240deg) translateX(30px); }
.loading-segment:nth-child(6) { --color: #ec4899; --i: 5; transform: rotate(300deg) translateX(30px); }

@keyframes loadingRotate {
    0%, 100% { opacity: 0.3; transform: rotate(calc(var(--i) * 60deg)) translateX(30px) scale(1); }
    50% { opacity: 1; transform: rotate(calc(var(--i) * 60deg)) translateX(30px) scale(1.2); }
}

.loading-logo {
    width: 350px;
    height: auto;
    margin-bottom: 50px;
}

.loading-text {
    font-size: 16px;
    color: #fff;
    letter-spacing: 1px;
}

.loading-progress {
    width: 200px;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    overflow: hidden;
}

.loading-progress-bar {
    height: 100%;
    background: linear-gradient(90deg, #4a9eff, #6dd400, #a855f7);
    background-size: 200% 100%;
    animation: loadingProgress 1.5s ease-in-out infinite;
}

@keyframes loadingProgress {
    0% { width: 0%; background-position: 0% 0%; }
    50% { width: 70%; }
    100% { width: 100%; background-position: 100% 0%; }
}

/* =========================================
   Legenda dos Grupos
   ========================================= */

#groups-legend {
    position: fixed;
    top: 280px;
    left: 80px;
    background: rgba(20, 20, 40, 0.9);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border-radius: 12px;
    padding: 15px;
    min-width: 180px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3);
